import React, { useEffect } from "react";
import { LinkContainer } from "react-router-bootstrap";
import { Table, Button,Row,Col } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Message from "../Components/Message";
import Loader from "../Components/Loader";
import { useNavigate } from "react-router-dom";
import { listOrders } from "../actions/orderActions";

const OrderListScreen = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const ordersList = useSelector((state) => state.ordersList);
  const { loading, error, orders } = ordersList;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;  

  useEffect(() => {

    if(!userInfo.isAdmin){
        navigate('/login');
    }
    else{
        dispatch(listOrders());
    }
    
  },[dispatch,userInfo,navigate])  


  return (
    <>
    <h1>Orders</h1>
    {loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message> : (
        <Table striped bordered hover responsive className="table-sm">
            <thead>
                <tr>
                    <th>ORDER ID</th>
                    <th>USER ID</th>
                    <th>USER NAME</th>
                    <th>TOTAL PRICE</th>
                    <th>PAID</th>
                    <th>DELIVERED</th>
                    <th></th>
                </tr>
            </thead> 
            <tbody>
                {orders.map((order) => (
                    <tr key={order._id}>
                        <td>{order.user._id}</td>
                        <td>{order.user._id}</td>
                        <td>{order.user.name}</td>
                        <td>${order.totalPrice}</td>
                        <td>{order.isPaid ? <i className="fa-solid fa-check" style={{"color": "green"}} />: <i className="fa-solid fa-xmark" style={{"color" : "red"}}/>}</td>
                        <td>{order.isDelivered ? <i className="fa-solid fa-check" style={{"color": "green"}} />: <i className="fa-solid fa-xmark" style={{"color" : "red"}}/>}</td>
                        <td>
                            Details
                        </td>
                    </tr>
                ))
                }
            </tbody>
        </Table>
    )
    }
    </>
  );
};

export default OrderListScreen;
