import React from 'react'


const Rating = ({value,text}) => {
  return (
    <div classNameName='rating'>
        
        {/* 1st star*/}
        <span>
        <i className= {value >= 1 ? 'fas fa-star' : value >= 0.5 ? 'fas fa-star-half-alt' : 'fa-regular fa-star-sharp'}/>
        </span>

        {/* 2nd star*/}
        <span>
        <i className= {value >= 2 ? 'fas fa-star' : value >= 1.5 ? '' : 'fa-regular fa-star-sharp'}/>
        </span>

        {/* 3rd star*/}
        <span>
        <i className= {value >= 3 ? 'fas fa-star' : value >= 2.5 ? 'fa-regular fa-star-half-stroke' : 'fa-regular fa-star-sharp'}/>
        </span>

        {/* 4th star*/}
        <span>
        <i className= {value >= 4 ? 'fas fa-star' : value >= 3.5 ? 'fa-regular fa-star-half-stroke' : 'fa-regular fa-star-sharp'}/>
        </span>

        {/* 5th star*/}
        <span>
        <i className= {value > 5 ? 'fas fa-star' : value > 4.5 ? 'fa-regular fa-star-half-stroke' : 'fa-regular fa-star-sharp'}/>
        </span>
    </div>
  )
}

export default Rating
