import { PRODUCT_LIST_FAIL , PRODUCT_LIST_SUCCESS , PRODUCT_LIST_REQUEST, PRODUCT_DETAILS_REQUEST, PRODUCT_DETAILS_SUCCESS, PRODUCT_DETAILS_FAIL, PRODUCT_DELETE_REQUEST, PRODUCT_DELETE_SUCCESS, PRODUCT_DELETE_FAIL, PRODUCT_CREATE_REQUEST, PRODUCT_CREATE_SUCCESS, PRODUCT_CREATE_FAIL } from "../constants/productConstants";
import axios from 'axios';

export const listProducts = () => async (dispatch) => {
    try {
        
        dispatch({type:PRODUCT_LIST_REQUEST})
        const {data} = await axios.get('/api/products');

        dispatch({
            type:PRODUCT_LIST_SUCCESS,
            payload: data
        })

    } catch (error) {
        dispatch({
            type: PRODUCT_LIST_FAIL,
            payload: error.response && error.response.message ? error.response.data.message : error.message
        })
    }
}

export const listProductDetails = (id) => async (dispatch) => {
    try {
        
        dispatch({type:PRODUCT_DETAILS_REQUEST})
        const {data} = await axios.get(`/api/products/${id}`);

        dispatch({
            type:PRODUCT_DETAILS_SUCCESS,
            payload: data
        })

    } catch (error) {
        dispatch({
            type: PRODUCT_DETAILS_FAIL,
            payload: error.response && error.response.message ? error.response.data.message : error.message
        })
    }
}

export const deleteProducts = (id) => async (dispatch,getState) => {
    try {
      dispatch({
        type: PRODUCT_DELETE_REQUEST,
      });
  
      const {userLogin: {userInfo}} = getState();
  
      const config = {
        headers: {
          Authorization: `Bearer ${userInfo.token}`
        } ,
      };
  
      await axios.delete(
        `/api/products/${id}`,
        config
      );
  
      dispatch({
        type: PRODUCT_DELETE_SUCCESS
      });
  
    } catch (error) {
      dispatch({
        type: PRODUCT_DELETE_FAIL,
        payload:
          error.response && error.response.data
            ? error.response.data.message
            : error.meassage,
      });
    }
  };

  export const createProducts = () => async (dispatch,getState) => {
    try {
      dispatch({
        type: PRODUCT_CREATE_REQUEST,
      });
  
      const {userLogin: {userInfo}} = getState();
  
      const config = {
        headers: {
          Authorization: `Bearer ${userInfo.token}`
        } ,
      };
  
      const {data} = await axios.post(
        `/api/products`, {},
        config
      );
  
      dispatch({
        type: PRODUCT_CREATE_SUCCESS,
        payload: data
      });
  
    } catch (error) {
      dispatch({
        type: PRODUCT_CREATE_FAIL,
        payload:
          error.response && error.response.data
            ? error.response.data.message
            : error.meassage,
      });
    }
  };
