import { ORDER_CREATE_FAIL,ORDER_CREATE_SUCCESS,ORDER_CREATE_REQUEST, ORDER_DETAILS_REQUEST, ORDER_DETAILS_SUCCESS, ORDER_DETAILS_FAIL, ORDER_PAY_REQUEST, ORDER_PAY_SUCCESS, ORDER_PAY_FAIL, ORDER_LIST_MY_REQUEST, ORDER_LIST_MY_SUCCESS, ORDER_LIST_MY_FAIL, ORDER_LIST_REQUEST, ORDER_LIST_SUCCESS, ORDER_LIST_FAIL } from "../constants/orderConstants"
import axios from "axios"

export const createOrder = (order) => async (dispatch,getState) => {
    try {
      dispatch({
        type: ORDER_CREATE_REQUEST,
      });
  
      const {userLogin: {userInfo}} = getState();
  
      const config = {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${userInfo.token}`
        },
      };
  
      const { data } = await axios.post(
        `/api/orders`,order,
        config
      );
  
      dispatch({
        type: ORDER_CREATE_SUCCESS,
        payload: data,
      });
  
    } catch (error) {
      dispatch({
        type: ORDER_CREATE_FAIL,
        payload:
          error.response && error.response.data
            ? error.response.data.message
            : error.meassage,
      });
    }
  };

