import React from "react";
import PropTypes from "prop-types";

const Rating = ({ value, text, color }) => {
  return (
    <div className="rating">
      {/* 1st star*/}
      <span>
        <i
          style={{ color }}
          className={
            value >= 1
              ? "fas fa-star"
              : value >= 0.5
              ? "fas fa-star-half-alt"
              : "far fa-star"
          }
        />
      </span>

      {/* 2nd star*/}
      <span>
        <i
          style={{ color }}
          className={
            value >= 2
              ? "fas fa-star"
              : value >= 1.5
              ? "fas fa-star-half-alt"
              : "far fa-star"
          }
        />
      </span>

      {/* 3rd star*/}
      <span>
        <i
          style={{ color }}
          className={
            value >= 3
              ? "fas fa-star"
              : value >= 2.5
              ? "fas fa-star-half-alt"
              : "far fa-star"
          }
        />
      </span>

      {/* 4th star*/}
      <span>
        <i
          style={{ color }}
          className={
            value >= 4
              ? "fas fa-star"
              : value >= 3.5
              ? "fas fa-star-half-alt"
              : "far fa-star"
          }
        />
      </span>

      {/* 5th star*/}
      <span>
        <i
          style={{ color }}
          className={
            value > 5
              ? "fas fa-star"
              : value > 4.5
              ? "fas fa-star-half-alt"
              : "far fa-star"
          }
        />
      </span>

      <span id="review" className="ms-auto">
        {text}
      </span>
    </div>
  );
};

Rating.defaultProps = {
  color: "#f8e825",
};

Rating.propTypes = {
  text: PropTypes.string.isRequired,
  color: PropTypes.string,
};

export default Rating;
