import React from 'react'

const Rating = ({value,text}) => {
  return (
    <div classNameName='rating'>
        
        {/* 1st star*/}
        <span>
        <i className= {value >= 1 ? "fa-solid fa-star-sharp" : value > 0.5 ? "fa-regular fa-star-half-stroke" : "fa-regular fa-star-sharp"}></i>
        </span>

        {/* 2nd star*/}
        <span>
        <i className= {value > 2 ? "fa-solid fa-star-sharp" : value > 1.5 ? "fa-regular fa-star-half-stroke" : "fa-regular fa-star-sharp"}></i>
        </span>

        {/* 3rd star*/}
        <span>
        <i className= {value > 3 ? "fa-solid fa-star-sharp" : value > 2.5 ? "fa-regular fa-star-half-stroke" : "fa-regular fa-star-sharp"}></i>
        </span>

        {/* 4th star*/}
        <span>
        <i className= {value > 4 ? "fa-solid fa-star-sharp" : value > 3.5 ? "fa-regular fa-star-half-stroke" : "fa-regular fa-star-sharp"}></i>
        </span>

        {/* 5th star*/}
        <span>
        <i className= {value > 5 ? "fa-solid fa-star-sharp" : value > 4.5 ? "fa-regular fa-star-half-stroke" : "fa-regular fa-star-sharp"}></i>
        </span>
    </div>
  )
}

export default Rating
