import React, {useState,useEffect} from 'react';
import {Link} from 'react-router-dom';
import {Form,Button,Row,Col} from 'react-bootstrap';
import {useDispatch,useSelector} from 'react-redux';
import Message from '../Components/Message';
import Loader from '../Components/Loader';
import { login } from '../actions/userActions'; 
import FormContainer from '../Components/FormContainer';
import { useSearchParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const LoginScreen = (history) => {

    const navigate = useNavigate();

    const[searchParams] = useSearchParams();
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');

    const dispatch = useDispatch();

    const userLogin = useSelector( state => state.userLogin);

    const {loading,error,userInfo} = userLogin;

    useEffect(() => {
        if(userInfo){
            navigate()
        }
    })

    const redirect = searchParams ? searchParams.get('redirect') : '/';

    const submitHandler = (e) => {
        e.preventDefault();
    }

  return (
    <FormContainer>

        <h1> Sign In </h1>
        <Form onSubmit={submitHandler}>
            <Form.Group className='formGroup' controlId='email'>
                <Form.Label>Email Address</Form.Label>
                    <Form.Control type='email' placeholder='Enter Email' value={email} onChange={(e) => setEmail(e.target.value)}></Form.Control>
            </Form.Group>
            <Form.Group className='formGroup' controlId='password'>
                <Form.Label>Password</Form.Label>
                    <Form.Control type='password' placeholder='Enter Password' value={password} onChange={(e) => setPassword(e.target.value)}></Form.Control>
            </Form.Group>

            <Button className='reactBootstrapButton' type='submit' variant='primary'>
                Sign In
            </Button>
        </Form>

        <Row className='py-3'>
            <Col>
                New Customer? <Link to={redirect ? `/register?redirect=${redirect}` : '/register'}>Register</Link>
            </Col>
        </Row>

    </FormContainer>
  )
}

export default LoginScreen
