import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Form, Button, Row, Col } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Message from "../Components/Message";
import Loader from "../Components/Loader";
import FormContainer from "../Components/FormContainer";
import { useSearchParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import { getUserDetails, updateUsers } from "../actions/userActions";
import { useParams } from "react-router-dom";
import { USER_UPDATE_RESET } from "../constants/userConstants";

const UserEditScreen = ({ history }) => {
  const params = useParams();
  const userId = params.id;
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [isAdmin, setIsAdmin] = useState(false);
  const [message, setMessage] = useState(null);

  const dispatch = useDispatch();

  const userDetails = useSelector((state) => state.userDetails);
  const { loading, error, user } = userDetails;

  const userUpdate = useSelector((state) => state.userUpdate);
  const {loading:loadingUpdate,error:errorUpdate,success:successUpdate} = userUpdate;

  useEffect(() => {
    if(successUpdate){
      dispatch({type: USER_UPDATE_RESET});
      navigate('/admin/userlist');
    }
    else{
      if (!user.name || user._id !== userId) {
        dispatch(getUserDetails(userId));
      } else {
        setName(user.name);
        setEmail(user.email);
        setIsAdmin(user.isAdmin);
      }
    }
  }, [dispatch, user, userId,successUpdate,navigate]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(updateUsers({_id:userId,name,email,isAdmin}));
  };

  return (
    <>
      <Link to="/admin/userlist">Go Back</Link>
      <FormContainer>
        <h1>Edit User </h1>
        {loadingUpdate && <Loader />}
        {}
        {loading ? (
          <Loader />
        ) : error ? (
          <Message variant="danger">{error}</Message>
        ) : (
          <Form onSubmit={submitHandler}>
            <Form.Group className="formGroup" controlId="name">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="name"
                placeholder="Enter Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group className="formGroup" controlId="email">
              <Form.Label>Email Address</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              ></Form.Control>
            </Form.Group>
            <Form.Group className="formGroup" controlId="isadmin">
              <Form.Check
                type="checkbox"
                label="Is Admin"
                checked={isAdmin}
                onChange={(e) => setIsAdmin(e.target.checked)}
              ></Form.Check>
            </Form.Group>
            <Button
              className="reactBootstrapButton"
              type="submit"
              variant="primary"
            >
              Update
            </Button>
          </Form>
        )}
      </FormContainer>
    </>
  );
};

export default UserEditScreen;
